INSERT INTO "INFO" VALUES(733,'Lola''s Math Train','Children ages 3-7 can enjoy 19 different basic math games that challenge them with addition, subtraction, puzzles, shapes, sequencing and matching. As the child improves their skills, the questions become more challenging; when additional practice is needed, the game will remain at the level until it has been mastered.','3DS','3DS','Mar 21, 2013','E','http://www.gamefaqs.com/3ds/707099-lolas-math-train');
