INSERT INTO "INFO" VALUES(961,'Garfield Kart','Garfield burns up the road with a new racing game from Anuman Interactive. Garfield Kart is a no holds barred cross-country race with the worlds most famous cat at the steering wheel.','3DS','3DS','Aug 11, 2015','E','http://www.gamefaqs.com/3ds/142225-garfield-kart');
