INSERT INTO "INFO" VALUES(1013,'ARC STYLE: Jazzy Billiards 3D Professional','ARC STYLE: Jazzy Billiards 3D Professional is a Sports game, developed and published by Arc System Works, which was released in Japan in 2011.','3DS','3DS','Aug 10, 2011',NULL,'http://www.gamefaqs.com/3ds/638717-arc-style-jazzy-billiards-3d-professional');
